const imgBox=[...document.querySelectorAll(".preview__img-box")];imgBox.forEach((e=>{e.addEventListener("mouseenter",(e=>{const i=e.target.querySelector(".preview__description");i.classList.contains("preview__description")&&i.classList.add("preview__description_hover")})),e.addEventListener("mouseleave",(e=>{const i=e.target.querySelector(".preview__description");i.classList.contains("preview__description")&&i.classList.remove("preview__description_hover")}))}));